import './anime.css'
import solar from '../../assets/animations/solar.json';
import Lottie from 'lottie-web';
import Marquee from "react-fast-marquee";


    export default function Anime () {
        React.useEffect(() => {
          Lottie.loadAnimation({
            container: document.querySelector("#anime"),
            animationData: solar,
            renderer: "svg", // "canvas", "html"
            loop: true, // boolean
            autoplay: true, // boolean

          });
        }, []);
      return(
        <div className='animationContent'>

             <div className='animationContainer' id='anime'>

             <div className='animationText'>
               <Marquee behavior="scroll" direction="left" speed={200}>
                <p>EXCELLENCE & CREATIVITY / <span>All In 1 </span> </p>
                </Marquee>
             </div>

             </div>

