import React from 'react';
import './header.css';


const Header = () => {

    const animationContainer = useRef(null)
  useEffect(() => {
    lottie.loadAnimation({
      animationData: require('./assets//animations/wavey.json')
    })
  }, [])

    return (
        <div className='malithebrand__header-container  '>
            <div className='malithebrand__header-top'>
            <div className='malithebrand__header-title'>
                <h1 className='malithebrand__header-name tracking-in-contract-bck'>akwunt√©</h1>
                <p>DESIGNER X DEVELOPER</p>
            </div>
            <div className='malithebrand__button'>
            <button className='jello-horizontal'>View portfolio</button>
            </div>
            </div>
            <div className='animationContainer' ref={animationContainer} />
        </div>
    )
}

export default Header
